import React from "react";

class Favorites extends React.Component {
  state = { list: [] };

  componentDidMount() {
    this.loadLocalStorage();
  }

  loadLocalStorage = () => {
    for (let key in this.state) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  };

  getFavorite = () => {
    const list = JSON.parse(localStorage.getItem("list"));
    return list;
  };

  onRenderFavorite = list => {
    const renderList = list.map(favorite => {
      return (
        <div className="item">
          <a className="ui tiny image">
            <img src="/images/avatar/large/veronika.jpg" />
          </a>
          <div className="content">
            <a className="header">Veronika Ossi</a>
            <div className="description">
              <p>
                Veronika Ossi is a set designer living in New York who{" "}
                <a>enjoys</a> kittens, music, and partying.
              </p>
            </div>
          </div>
        </div>
      );
    });
    return renderList;
  };

  render() {
    const listFavotires = this.getFavorite();
    return <div>{this.onRenderFavorite(listFavotires)}</div>;
  }
}

export default Favorites;
