class MovieList extends React.Component {
  renderList(movies) {
    const listMovies = movies.map(movie => {
      return (
        <div>
          <MoviesItems movie={movie} onMovieSelect={movie.onMovieSelect} />
        </div>
      );
    });
    return listMovies;
  }
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return <Slider {...settings}>{this.renderList(this.props.movies)}</Slider>;
