import React from "react";
import Slider from "react-slick";

const MoviesItems = ({ movie, onMovieSelect }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (
    <Slider {...settings}>
      <div
        onClick={() => onMovieSelect(movie)}
        className="item"
        style={{ cursor: "pointer" }}
      >
        <img className="ui tiny image" src={movie.Poster} alt={movie.Title} />
        <div className="content">
          <div className="header">{movie.Title}</div>
          <div className="description">
            <p>{movie.Year}</p>
          </div>
        </div>
      </div>
    </Slider>
  );
};

export default MoviesItems;

<Slider {...settings}>
  <div>
    <h3>1</h3>
  </div>
  <div>
    <h3>2</h3>
  </div>
  <div>
    <h3>3</h3>
  </div>
  <div>
    <h3>4</h3>
  </div>
  <div>
    <h3>5</h3>
  </div>
  <div>
    <h3>6</h3>
  </div>
</Slider>;
