import React from "react";
import MoviesItems from "./MoviesItems";
import Slider from "react-slick";

class MovieList extends React.Component {
  renderList(movies) {
    const listMovies = movies.map(movie => {
      return (
        <div
          className="ui one column stackable center aligned page grid"
          key={movie.imdbID}
        >
          <div class="column twelve wide">Your stuff here</div>
        </div>
      );
    });
    return listMovies;
  }
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return <Slider {...settings}>{this.renderList(this.props.movies)}</Slider>;
  }
}
export default MovieList;
