class MovieList extends React.Component {
  rederedList = this.props.movies.map(movie => {
    return (
      <div>
        <MoviesItems
          movie={movie}
          key={movie.imdbID}
          onMovieSelect={movie.onMovieSelect}
        />
      </div>
    );
  });
  render() {
    console.log(this.props);
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return (
      <Slider {...settings}>
        <div>{rederedList}</div>
      </Slider>
    );
  }
}
export default MovieList;
