import React from "react";

class Favorites extends React.Component {
  state = { list: [] };

  componentDidMount() {
    this.loadLocalStorage();
  }

  loadLocalStorage = () => {
    // Se cargan todas las llaves que existan en localStorage
    for (let key in this.state) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  };

  getFavorite = () => {
    const list = JSON.parse(localStorage.getItem("list"));
    return list;
  };

  onRenderFavorite = list => {
    renderList = list.map(favorite => {
      return <div>{favorite.Title}</div>;
    });
  };

  render() {
    const listFavotires = this.getFavorite();
    console.log(listFavotires);

    return <div>{this.onRenderFavorite(listFavotires)}</div>;
  }
}

export default Favorites;
