import React from "react";
import MoviesItems from "./MoviesItems";
import Slider from "react-slick";

class MovieList extends React.Component {
  renderList(movies) {
    const listMovies = movies.map(movie => {
      return (
        <div key={movie.imdbID}>
          <MoviesItems movie={movie} onMovieSelect={movie.onMovieSelect} />
        </div>
      );
    });
    return listMovies;
  }
  render() {
    const configSlider = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return (
      <Slider {...configSlider}>{this.renderList(this.props.movies)}</Slider>
    );
  }
}
export default MovieList;
