import React from "react";
import omdbapi from "../api/omdbapi";
import MoviesList from "./MoviesList";
import { connect } from "react-redux";
import { fetchMovies } from "../actions";

class Movies extends React.Component {
  state = { movies: [], selectMovie: null };
  componentDidMount() {
    this.props.fetchMovies("Batman");
    this.onTermSumit("Batman");
  }
  onTermSumit = async term => {
    const response = await omdbapi.get("/", {
      params: {
        s: term,
        apikey: process.env.REACT_APP_OMDBAPI_KEY
      }
    });
    // console.log(response);
    this.setState({
      movies: response.data.Search
      //   selectMovie: response.data.Search[0]
    });
    // console.log(this.state.movies);
  };

  render() {
    console.log(this.props.movies);
    return (
      <div>
        <MoviesList movies={this.props.movies} />
      </div>
    );
  }
}

const mapStateToProps = ({ movies }) => {
  return { movies: movies.data };
};

export default connect(
  mapStateToProps,
  { fetchMovies }
)(Movies);
