import React from "react";
import { connect } from "react-redux";
import { fetchMovieDetail } from "../actions";

class MovieDetail extends React.Component {
  componentDidMount() {
    this.props.fetchMovieDetail("tt0372784");
  }
  render() {
    console.log(this.props.movie);

    return (
      <div className="ui grid">
        <div className="ui row">
          <div className="six wide column">
            <img className="ui image" src={this.props.movie.Poster} />
          </div>
          <div className="ten wide column">
            <div className="content">
              <div className="header">{this.props.movie.Title}</div>

              <div className="extra">Director: {this.props.movie.Director}</div>
              <div className="extra">Genero: {this.props.movie.Genre}</div>
              <div className="extra">Trama: {this.props.movie.Plot}</div>
              <div className="extra">Duraci√≥n: {this.props.movie.Runtime}</div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ movie }) => {
  return { movie: movie.data };
};

export default connect(
  mapStateToProps,
  { fetchMovieDetail }
)(MovieDetail);
